import { Component } from "react";
import '../css/home-emoji.css';
import ListOfEmojis from '../component/emoji-list.js';

class SearchSymbols extends Component{

    constructor(props){
        super(props);
        this.state = {
            EmojyName :""
        }
    }

    showEmojyName(item){
       
       let data = ListOfEmojis.filter(name => name.text === item)
       console.log(data)
        this.setState({EmojyName:data})
    }

    render(){

        return(
            <div> 
            <div className="emoji-search-heading-text">
                   <section> Are you looking for Emoji symbols? <br/>
                    If yes, <br/>
                    then you are on the right website because we have collected all Emoji symbols in one place .<br/>
                    </section>
                   
            </div>
                <div className="emoji-search-textfield-container">
                    <input type="text" name="search" placeholder="Search.." className="emoji-search-textfield"/>
                </div>
                      
                <div className="display-emoji">
                    {
                        ListOfEmojis.map((item,index)=>{
                           return(
                              <button id={item.text} className={item.text} onClick={()=>this.showEmojyName(item.text)}> 
                                   {item.Symbol}
                              </button>
                           )
                          
                        })
                    }

                        {this.state.EmojyName?this.state.EmojyName:""}

                </div>
            </div>
        )
    }
}

export default SearchSymbols;
