import { Component } from "react";
import '../css/home-emoji.css';
import ListOfEmojis from '../component/emoji-list.js';

class SearchSymbols extends Component{

    constructor(props){
        super(props);
        this.state = {
            EmojyName :"",
            List_Emoji : ListOfEmojis,
            displaySearchData:[]
        }
    }

    handleSearch=(e)=>{
              let getvalue = e.target.value
             let data = this.state.List_Emoji.filter(searchitem=> searchitem.text.includes(getvalue));
             console.log(data);
            //  this.setState({displaySearchData:data});
    }

    showEmojyName(item){
        this.setState({EmojyName:item})
    }

    render(){

        return(
            <div> 
            <div className="emoji-search-heading-text">
                   <section> Are you looking for Emoji symbols? <br/>
                    If yes, <br/>
                    then you are on the right website because we have collected all Emoji symbols in one place .<br/>
                    </section>
                   
            </div>
                <div className="emoji-search-textfield-container">
                    <input type="text" name="search" placeholder="Search.." className="emoji-search-textfield" onChange={this.handleSearch}/>
                </div>
                      
                <div className="displaySearchData"> 
                {this.state.displaySearchData ? 
                   this.state.displaySearchData.map((item,index)=>{
                       return(
                           <div>
                               {item.Symbol}
                           </div>
                       )
                   }) 
                   : ""}</div>

                <div className="display-emoji">
                    {
                        ListOfEmojis.map((item,index)=>{
                           return(
                              <button id={item.text} className={item.text} onClick={()=>this.showEmojyName(item.text)}> 
                                   {item.Symbol}
                              </button>
                           )
                          
                        })
                    }


                </div>
                <div className="display-emoji">
                    {this.state.EmojyName?this.state.EmojyName:"no name"}
                </div>
            </div>
        )
    }
}

export default SearchSymbols;
